import React,{Component} from "react";
import {connect} from "react-redux";
import {NavLink} from "react-router-dom";
import { Carousel,Icon,Alert,Button} from 'antd';
import "./course.css";
import actions from "../../store/action";
// import {courseList} from "../../store/action/course";
class Course extends Component{
    componentWillMount(){
        if(this.props.bannerData.length<=0){
            this.props.courseBanner();//派发指令拿到数据
        }
        if (this.props.courseData.length<=0){
            this.props.courseList();
        }
    }
    render(){
        let {bannerData,courseData,courseType,total,page,courseList,isLoading,courseLoading} = this.props;//解构
        return <div className="courseBox">
            {/*轮播图*/}
            {
                bannerData.length>0?<Carousel autoplay>
                    {
                        bannerData.map((item,index)=>{
                            return  <div key={index}>
                                <img src={item.pic} alt=""/>
                            </div>
                        })
                    }
                </Carousel>:null
            }
            {/*全部课程*/}
            <div className="couBox">
                <h2>
                    <Icon type="menu-fold"/>
                    <span>
                        {
                            courseType==="all"?"全部课程":
                                courseType==="react"?"REACT课程":
                                    courseType==="vue"?"vue课程":
                                        "小程序课程"
                        }
                    </span>
                </h2>
                <div className="courseList">
                    {
                        courseData.length>0?
                            courseData.map(function(item,index) {
                                let {name,dec,pic,time,id}=item;
                                return <div className="couItem" key={index}>
                                    <NavLink to={{
                                        pathname:"/course/info",
                                        search:`courseID=${id}`
                                    }}>
                                        <h3 className="title">{name} </h3>
                                        <div className="desc">
                                            <div className="imgBox">
                                                <img src={pic} alt=""/>
                                            </div>
                                            <p> {dec}</p>
                                            <span>时间：{time}</span>
                                        </div>
                                    </NavLink>
                                </div>
                            }):<Alert message="当前没有数据" type="error" showIcon/>
                    }
                    {isLoading ? <Button type="primary" loading className="loading">
                        Loading
                    </Button> : total<=page ? null : <a href="##" className="loadMore" onClick={(e)=>{
                        courseLoading();
                        courseList({page:parseInt(page)+1,type:courseType},false);
                    }}> 加载更多 </a>}
                </div>
            </div>
        </div>
    }
}
//执行actionCreator函数才表示派发指令
export  default  connect((state)=>{
    return {...state.course}
},actions.course)(Course);
