import * as Types from "../action-types";
import {getBanner,getList} from "../../api/course";
//定义actionCreate方法
let courseBanner = ()=>{
    return async (dispatch)=>{
        let {data} = await getBanner();
        dispatch({type:Types.COURSE_BANNER,data});
    }
};
/*let courseList = (params={limit:10,type:"all",page:1})=>{
    return async (dispatch)=>{
        let result = await getList(params);
        dispatch({type:Types.COURSE_LIST,result,courseType:params.type});
    }
};*/
//params={type:"all",page:1,limit:10}设置默认值
let courseList=(params={},replace=true)=>{
    params={
        limit:10,
        page:1,
        type:'all',
        ...params,//用用户传过来的值替换默认值
    };
    return async (dispatch)=>{
        let result =await getList(params);
        dispatch({type:Types.COURSE_LIST,result,courseType:params.type,isLoading:true,replace});
    }
};
let courseLoading = ()=>{
    return {
        type:Types.COURSE_IS_LOADING
    }
};

export {courseBanner,courseList,courseLoading};
