//跨域请求
import axios from "axios";
import qs from "qs";
axios.defaults.baseURL = "http://localhost:8000"; //请求的接口的公共路径
axios.defaults.withCredentials = true; //允许请求时能够携带数据凭证（例如cookie）
axios.interceptors.response.use((res)=>{
    return res.data; //对响应的数据进行劫持
});
//发送的数据必须是url-encode字符串的形式
//处理请求的数据：fetch（原生api方法，可以直接用，返回的是promise对象，不好用）
axios.defaults.transformRequest = (data)=>{
    return qs.stringify(data);//把对象转换成url-encode字符串的形式
};
export default axios;
