import React,{Component} from "react";
import {connect} from "react-redux";
import "./unPay.css"
import actions from "../../store/action";
import {} from "../../store/action "
class UnPay extends Component{
    componentWillMount(){
        if (this.props.list.length <= 0){
            this.props.cartList();
        }
    }
    remove(){

    }
    render(){
        return <div className="unpayBox">
            <div className="bar">
                <a href="##" onClick={()=>{this.props.updateSelected(true)}}>全选</a>
                <a href="##" onClick={()=>{this.props.updateSelected(false)}}>全不选</a>
                <a href="##" onClick={()=>{this.remove}}>删除</a>
                <a href="##">支付</a>
            </div>
            <ul className="infoList">
                {
                    this.props.list.length>0 && this.props.list.map((item,index)=>{
                        return <li key={index}>
                            <input type="checkBox" checked={item.isSelected} onChange={this.props.update}/>
                            <span> {item.name} </span>
                        </li>
                    })
                }
            </ul>
        </div>
    }
}
export default connect((state)=>({...state.cart}),actions.cart)(UnPay);
