import axios from "./index";
//获取购物车列表
export const getCartList = (params={state:0,_:Math.random})=>{
    return axios.get("/store/info",{params});
};
//将某个课程添加到购物车
export const addCartList = (courseID)=>{
    return axios.post("/store/add",{courseID})
};
//删除购物车中某个课程
export const removeCart = (courseID)=>{
    return axios.post("/store/remove",{courseID})
};
